&lt;/ul&gt;

185---TTRPG/050---Pathfinder/001---Chara.content:
const { Pathfinder } = customJS;

const level = dv.page(&quot;002 - Levels&quot;).Level;

const abilities = Pathfinder.getAbilitiesObject({
    dv,
    path: &quot;010 - Abilities&quot;,
});

const proficiencies = Pathfinder.getSkillsProficienciesObject({
    dv,
    path: &quot;021 - Skills Details&quot;,
});

Pathfinder.renderSkills({
    dv,
    abilities,
    proficiencies,
    level,
});
Custom

[Lore: Academia:: 5]

