&lt;figure data-rehype-pretty-code-figure=&quot;&quot;&gt;&lt;pre tabindex=&quot;0&quot; data-language=&quot;dataviewjs&quot; data-theme=&quot;github-light github-dark&quot;&gt;&lt;code data-language=&quot;dataviewjs&quot; data-theme=&quot;github-light github-dark&quot; style=&quot;display: grid;&quot;&gt;&lt;span data-line=&quot;&quot;&gt;&lt;span&gt;const current = dv.current();&lt;/span&gt;&lt;/span&gt;
