const current = dv.current();

const query = `TASK FROM &quot;${current.file.folder}&quot; WHERE contains(tags, &quot;#Character/Trait&quot;)`;

const feats = await dv.query(query);
const featsText = feats.value.values
    .map(f =&gt; f.text);

dv.list(featsText);

